// KAMUI CODE MCP Client - JavaScript/TypeScript
import { MCPClient } from '@kamui/mcp-client';

// クライアントの初期化
const client = new MCPClient({
  baseUrl: 'https://api.kamui-code.com',
  apiKey: process.env.KAMUI_API_KEY
});

// 画像生成の例
async function generateImage() {
  try {
    const result = await client.creative.text2image({
      prompt: '美しい夕焼けの富士山',
      model: 'flux-ultra',
      size: '1024x1024',
      steps: 30
    });

    console.log('生成された画像URL:', result.imageUrl);
    return result;
  } catch (error) {
    console.error('画像生成エラー:', error);
  }
}

// 動画生成の例
async function generateVideo() {
  const result = await client.creative.text2video({
    prompt: '桜が舞う春の公園',
    model: 'haiper-standard',
    duration: 5,
    fps: 30
  });

  return result.videoUrl;
}

// コード生成の例
async function generateCode() {
  const result = await client.development.generateCode({
    prompt: 'React Hookを使用したカウンターコンポーネント',
    language: 'typescript',
    framework: 'react'
  });

  return result.code;
}

// 要件定義書生成の例
async function generateRequirements() {
  const result = await client.business.generateRequirements({
    projectName: 'ECサイトリニューアル',
    features: [
      'ユーザー認証',
      '商品検索',
      '決済機能',
      'レコメンド機能'
    ],
    targetUsers: 'BtoC、20-40代',
    deadline: '2025-12-31'
  });

  return result.document;
}
